import {useSelector, useDispatch} from "react-redux"
import {useEffect} from 'react'
import {useNavigate} from "react-router-dom"
import {useAlert} from 'react-alert'
import Post from "../../components/Post/Post"
import "./profile.css"
import { getMyPosts } from "../../redux/actions/getMyPostsAction"
import Loading from "../../components/Loading/Loading"
import { loadUser } from "../../redux/actions/userAction"

const Profile = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const alert = useAlert()
    const user = useSelector(state => state.user)
    console.log(user.user._id)
    const postLike = useSelector(state => state.likePost)
    const comment = useSelector(state => state.comment)
    const getAllMyPosts = useSelector(state => state.getMyPosts)
    const {loading, myPosts} = getAllMyPosts


    useEffect(() => {
        dispatch(getMyPosts())
        dispatch(loadUser())
        if(!user.user._id){
            navigate("/")
        }
    }, [dispatch, navigate, user.user._id])

    useEffect(() => {
        if(postLike.error) {
            alert.error(postLike.error)
        }
        if(postLike.msg){
            alert.show(postLike.msg)
        }

        if(comment.error) {
            alert.error(comment.error)
        }
        if(comment.commentStatusMsg){
            alert.show(comment.commentStatusMsg)
        }
    }, [alert, postLike.error, postLike.msg, comment.commentStatusMsg, comment.error])

    return (
        loading ? (<Loading />) : (
            <div>
                <div className="profile">
                    <div className="profile_container">
                        <div className="img_section">
                            <img className="background_photo" src="https://images.pexels.com/photos/2356045/pexels-photo-2356045.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1" alt="bg_photo" />
                            <img className="display_pic" src="https://images.pexels.com/photos/11389730/pexels-photo-11389730.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" alt="dp" />
                        </div>
                        <div className="user_property_section">
                            <div className="user_details">
                                <h2 className="user_info">User Information</h2>
                                <span className="info"><strong>First Name</strong> : {}</span>
                                <span className="info"><strong>Last Name</strong> : {}</span>
                                <span className="info"><strong>Followers</strong>  : {user.user.followers.length}</span>
                                <span className="info"><strong>Followings</strong> : {user.user.followings.length}</span>
                            </div>
                            <div className="user_post">
                                {myPosts && myPosts.length > 0 ? myPosts.map((myEachPost) => (
                                    <Post key={myEachPost._id} post={myEachPost} loading={loading} />
                                )) : <p>You have no post yet.</p>}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    )
}

export default Profile
