import Sidebar from "../../components/Sidebar/Sidebar"
import Feed from "../../components/Feed/Feed"
import Rightbar from "../../components/Rightbar/Rightbar"
import "./home.css"

import {useDispatch, useSelector} from "react-redux"
// import {useNavigate} from "react-router-dom"
import {useEffect} from "react"
import { getPostsOfFollowings } from "../../redux/actions/postAction"

const Home = () => {
    const dispatch = useDispatch()
    // we are collecting all posts of people whom we have followed
    const getAllPostsOfFollowing = useSelector(state => state.posts)
    const {posts} = getAllPostsOfFollowing;


    // const navigate = useNavigate()

    // useEffect(() => {

    //     if(!auth._id) {
    //         navigate("/")
    //     }
    // }, [auth._id, navigate])



    useEffect(() => {
        dispatch(getPostsOfFollowings());
    }, [dispatch])

    return (
        <div>
            <div className="home">
                <div className="home_container">
                    <Sidebar />
                    {!posts ? (<div className="no_post">No posts yet</div>) : <Feed posts={posts} />}
                    <Rightbar />
                </div>
            </div>
        </div>
    )
}

export default Home
