import {
    COMMENT_REQUEST,
    COMMENT_SUCCESS,
    COMMENT_FAILURE,
    GET_COMMENT_REQUEST,
    GET_COMMENT_SUCCESS,
    GET_COMMENT_FAILURE
} from '../constants/actionTypes'
import axios from 'axios'


export const getAllCommentOfPost = (postID) => async (dispatch) => {
    try {
        try {
            dispatch({
                type : GET_COMMENT_REQUEST
            })

            const {data} = await axios.get(`/post/allComments/${postID}`)
            console.log(data)

            dispatch({
                type : GET_COMMENT_SUCCESS,
                payload : data.comments
            })
        } catch (error) {
            dispatch({
                type : GET_COMMENT_FAILURE,
                payload : error.response.data.msg
            })
        }
    } catch (error) {
        console.log(error);
    }
}


export const commentOnPost = (postId, comment) => async (dispatch) => {
    try {
        try {
            dispatch({
                type : COMMENT_REQUEST,
            })

            const {data} = await axios.put(`/post/comment/${postId}`, {comment}, {header : {"content-type" : "application/json"}})
            console.log(data)

            dispatch({
                type : COMMENT_SUCCESS,
                payload : data.msg
            })
        } catch (error) {
            dispatch({
                type : COMMENT_FAILURE,
                payload : error.response.data.msg
            })
        }
    } catch (error) {
        console.log(error)
    }
}
