import React, {useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import Users from '../../components/Users/Users'
// import { allUsersList } from '../../redux/actions/allUsersActio
import { searchUser } from '../../redux/actions/searchAction'
import "./search.css"

const Search = () => {
    const dispatch = useDispatch()
    const allUsers = useSelector(state => state.allUsers)
    const [searchTerm, setSearchTerm] = useState("")


    const filteredUser = () => {
        allUsers.users.filter((user) => {
            if(searchTerm === "") {
                return user
            } else if (user.userName.toLowerCase().includes(searchTerm.toLowerCase())) {
                return user
            }
        })
    }


    return (
        <div className="search">
            <div className="search_container">
                <h2 style={{textAlign : 'center'}}>Search User</h2>
                <form className="search_form" >
                    <input 
                     className="input_field" 
                     placeholder="search user" 
                     type="text"
                     value={searchTerm}
                     onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    {/* <button className="search_btn" type='submit'>Search</button> */}
                </form>

                <div className="appreance_of_users">
                    {filteredUser().map((user) => (
                        <Users key={user._id} userId={user._id} userName={user.userName} userDp={user.avatar ? user.avatar.url : null} />

                    ))}
                </div>
            </div>
        </div>
    )
}

export default Search
