import {REGISTER_REQUEST, REGISTER_FAILURE, LOGIN_REQUEST, LOGIN_SUCCESS, LOGIN_FAILURE, REGISTER_SUCCESS, LOGOUT_REQUEST, LOGOUT_SUCCESS, LOGOUT_FAILURE} from "../constants/actionTypes"
import axios from "axios"

export const register = (newUserData) => async (dispatch) => {
    try {
        dispatch({
            type : REGISTER_REQUEST
        });

        const {data} = await axios.post("/auth/register", newUserData, {headers : {"Content-Type" : "application/json,"}});

        dispatch({
            type : REGISTER_SUCCESS,
            payload : data.newUser
        });
    } catch (error) {
        console.log(error);
        dispatch({
            type : REGISTER_FAILURE,
            payload : error.message
        });
    }
};

export const login = (email, password) => async (dispatch) => {
    try {
        try {
            dispatch({
                type : LOGIN_REQUEST,
            });

            const {data} = await axios.post("/auth/login", {email, password}, {headers : {"Content-Type" : "application/json"}});

            dispatch({
                type : LOGIN_SUCCESS,
                payload : data.existingUser
            });
        } catch (error) {
            dispatch({
                type : LOGIN_FAILURE,
                payload : error.response.data.msg
            })
        }
    } catch (error) {
        console.log(error);
    }
};

export const logout = () => async (dispatch) => {
    try {
        dispatch({
            type : LOGOUT_REQUEST
        });

        const removeCookie = await axios.get("/auth/logout");

        dispatch({
            type : LOGOUT_SUCCESS,
            payload : removeCookie
        })
    } catch (error) {
        console.log(error);
        dispatch({
            type : LOGOUT_FAILURE,
            payload : error.message
        })
    }
}
