import {REGISTER_REQUEST, REGISTER_FAILURE, LOGIN_REQUEST, LOGIN_SUCCESS, LOGIN_FAILURE, REGISTER_SUCCESS, LOGOUT_REQUEST, LOGOUT_SUCCESS, LOGOUT_FAILURE} from "../constants/actionTypes"

const initialState = {
    _id : "",
    userName : "",
    firstName : "",
    lastName : "",
    posts : "",
    followers : "",
    followings : "",
    email : "",
    error : "",
    loading : true,
}

export const authReducer = (state = initialState, action) => {
    switch(action.type) {
        case REGISTER_REQUEST:
            return state.loading;

        case REGISTER_SUCCESS:
            const registeredUser = action.payload;
            return {...state, loading : false, _id : registeredUser._id, userName : registeredUser.userName, firstName : registeredUser.firstName, lastName : registeredUser.lastName, posts : registeredUser.posts, followings : registeredUser.followings, followers : registeredUser.followers};

        case REGISTER_FAILURE:
            return {...state, loading : false, error : action.payload};

        case LOGIN_REQUEST:
            return state.loading;

        case LOGIN_SUCCESS:
            const loggedInUser = action.payload;
            return {...state, loading : false, _id : loggedInUser._id, userName : loggedInUser.userName, firstName : loggedInUser.firstName, lastName : loggedInUser.lastName, posts : loggedInUser.posts, followings : loggedInUser.followings, followers : loggedInUser.followers};

        case LOGIN_FAILURE:
            return action.payload;

        case LOGOUT_REQUEST:
            return state.loading;

        case LOGOUT_SUCCESS:
            return action.payload;

        case LOGOUT_FAILURE:
            return action.payload;

        default:
            return state;
    }
}
