import { Delete, Edit, FavoriteBorder, Favorite, Forum , Cancel} from '@material-ui/icons'

import {Dialog, DialogTitle, DialogContent, DialogActions, Button} from '@material-ui/core'
import {useParams} from 'react-router-dom'
import {useSelector, useDispatch} from 'react-redux'
import {useState, useEffect} from 'react'

import "./post.css"
import Loading from '../Loading/Loading'
import { likePost } from '../../redux/actions/likePostAction'
import { getPostsOfFollowings } from '../../redux/actions/postAction'
import { comment } from '../../redux/actions/commentAction'

const Post = ({post, loading}) => {
    const params = useParams()
    const dispatch = useDispatch()
    const auth = useSelector(state => state.auth)
    const [like, setLike] = useState(false)
    const [commentText, setCommentText] = useState("")
    const [openCommentBox, setOpenCommentBox] = useState(false)
    const postClass = ["post"];

    if(params.id) {
        postClass.push("profile_post");
    }

    const handleLike = () => {
        setLike(!like);
        dispatch(likePost(post._id))

        dispatch(getPostsOfFollowings())
    }

    const commentSubmitHandler = (e) => {
        e.preventDefault()
        dispatch(comment(post._id, commentText))
        setCommentText("");
    }

    useEffect(() => {
        post.likes.forEach(item => {
            if(auth._id === item._id) {
                setLike(true);
            }
        });
    }, [auth._id, post.likes]) 


    return (
        loading === true ? <Loading /> : (
            <div>
                <div className={postClass.join(" ")}>
                    <div className="post_wrapper">
                        <div className="post_top">
                            <div className="post_top_left_corner">
                                <img className="user_photo" src="https://images.pexels.com/photos/11389730/pexels-photo-11389730.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" alt="user_photo" />
                                <span className="user_fullname">{post.user.userName}</span>
                            </div>
                            {auth._id === post.user ? (
                                <div className="post_top_right_corner">
                                    <Edit fontSize="small" />
                                    <Delete fontSize="small" color="secondary" />
                                </div>
                            ) : null}

                        </div>
                        <div className="post_center">
                            <span className="caption">{post.caption}</span>
                            <img className="post_picture" src="https://images.pexels.com/photos/11341064/pexels-photo-11341064.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" alt="post_picture" />
                        </div>
                        <div className="post_bottom">
                            <div className="post_bottom_left_corner">
                                <div className="like" onClick={handleLike}>
                                    {like ? <Favorite fontSize="small" style={{color : "red"}}/> : <FavoriteBorder fontSize="small" />}

                                </div>
                                    <span className="like_count">{post.likes.length} likes</span>
                            </div>
                            <div className="post_bottom_right_corner">
                                <div className="comment">
                                    <span className="comment_count">{post.comments.length}</span>
                                    <Forum fontSize="small" onClick={() => setOpenCommentBox(true)} />
                                </div>
                            </div>
                        </div>
                        <Dialog open={openCommentBox} aria-labelledby='form-dialog-title'>
                            <DialogTitle style={{display : "flex", justifyContent:"space-between"}}>
                                <h3>Comments</h3>
                                <button onClick={() => setOpenCommentBox(false)}>
                                    <Cancel fontSize="small" />
                                </button>
                            </DialogTitle>
                            <DialogContent>
                                <form onSubmit={commentSubmitHandler}>
                                    <input 
                                        type="text" 
                                        className="form_input_field" 
                                        placeholder="enter your comment..." 
                                        value={commentText} 
                                        onChange={(e) => setCommentText(e.target.value)}
                                    />
                                    <DialogActions>
                                        <Button type="submit" color="secondary" size="small" variant="contained">comment</Button>
                                    </DialogActions>
                                    </form>
                                    {post.comments > 0 ? (
                                        <div className="comment_section">
                                            <span className="folks_who_have_commented">{post.comments.user.userName}</span>
                                        </div>
                                    ) : null}
                            </DialogContent>
                        </Dialog>
                    </div>
                </div>
            </div>  
        )

    )
}

export default Post
