                dispatch(getPostsOfFollowings())
            }
        });
    }, [auth._id, posts, dispatch, alert])

    return (
        <div>
            <div className="home">
                <div className="home_container">
                    <Sidebar />
                    <Feed posts={posts} loading={loading} />
