import {
    GET_MY_POST_REQUEST,
    GET_MY_POST_SUCCESS,
    GET_MY_POST_FAILURE
} from "../constants/actionTypes"

const initialState = {
    loading : true,
    error : "",
    user : ""
}

export const meAndMyPersonalPostReducer = (state = initialState, action) => {
    switch(action.type) {
        case GET_MY_POST_REQUEST:
            return {...state, error : null, user : null}
        case GET_MY_POST_SUCCESS:
            return {...state, loading : false, error : null, user : action.payload}
        case GET_MY_POST_FAILURE:
            return {...state, loading : false, error : action.payload, user : null}
        default:
            return state;
    }
}
